mapper.select:
select s.*, c.name as categoryName
from setmeal s left join category c on s.category_id = c.id


        and s.name like concat('%',#{name},'%')


        and s.category_id = #{categoryId}


        and s.status = #{status}


order by s.create_time desc

mapper.update:
update setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select s.*, c.name as categoryName
from setmeal s left join category c on s.category_id = c.id
where s.id = #{id}

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc

select count(id) from setmeal


        and status = #{status}


        and category_id = #{categoryId}
